// External Imports
import React, { Component } from 'react';
import * as d3 from 'd3';
import * as topojson from 'topojson';

// Internal Imports
import './Choropleth.css';

// Data
import eapCountryData from '../../../data/ChoroplethData/EAPMap_topojson.json';

class Choropleth extends Component {
  state = {
    color: null,
    eapCountryData: null,
    windowHeight: null,
    windowWidth: null,
  };

  componentDidMount() {
    this.setState(
      {
        color: d3.scaleQuantize().domain([0, 10]).range(d3.schemeBlues[9]),
        eapCountryData: eapCountryData,
        windowHeight: window.outerHeight,
        windowWidth: window.outerWidth,
      },
      () => {
        this.renderMap();
      }
    );
  }

  renderMap = () => {
    const { eapCountryData, windowHeight, windowWidth } = this.state;
    const featureCollection = topojson.feature(
      eapCountryData,
      eapCountryData.objects.eap_all_by_subnatid1_shapefile
    );

    const svg = d3
      .select('.Choropleth-container')
      .append('svg')
      .attr('height', windowHeight)
      .attr('width', windowWidth);

    const center = d3.geoPath().centroid(featureCollection);
    const scale = 500;
    const offset = [windowWidth / 2, windowHeight / 2];

    const projection = d3
      .geoMercator()
      .scale(scale)
      .center(center)
      .translate(offset);

    const path = d3.geoPath().projection(projection);

    svg
      .append('g')
      .attr('class', 'country')
      .selectAll('path')
      .data(featureCollection.features)
      .enter().append('path')
        .attr("class", "county")
        .attr("fill", d => color(yearTempData.get(d.id)))
        .attr('d', path);
  };

  render() {
    return <div className="Choropleth-container" />;
  }
}

export default Choropleth;
