// External Imports
import React, { Component } from 'react';
import * as d3 from 'd3';
import * as topojson from 'topojson';

// Internal Imports
import './Choropleth.css';

// Data
import eapCountryData from '../../../data/ChoroplethData/EAPMap_topojson.json';
import geoJsonData from '../../../data/ChoroplethData/EAPMap_geojson.json';

class Choropleth extends Component {
  state = {
    eapCountryData: null,
    windowHeight: null,
    windowWidth: null,
  };

  componentDidMount() {
    console.log(eapCountryData);
    this.setState(
      {
        eapCountryData: eapCountryData,
        windowHeight: window.outerHeight,
        windowWidth: window.outerWidth,
      },
      () => {
        this.renderMap();
      }
    );
  }

  renderMap = () => {
    const { eapCountryData, windowHeight, windowWidth } = this.state;
    const eapCountries = topojson.feature(
      eapCountryData,
      eapCountryData.objects.eap_all_by_subnatid1_shapefile
    ).features;

    const center = d3.geoPath().centroid(eapCountries);
    console.log(center);

    const projection = d3
      .geoMercator()
      .translate([windowWidth / 4.5, windowHeight / 2]);

    const path = d3.geoPath().projection(projection);

    const svg = d3
      .select('.Choropleth-container')
      .append('svg')
      .style('height', windowHeight + 2000 + 'px')
      .style('width', windowWidth + 'px')
      .append('g');

    svg
      .selectAll('.country')
      .data(eapCountries)
      .enter()
      .append('path')
      .attr('class', 'country')
      .attr('d', path);
  };

  render() {
    return <div className="Choropleth-container" />;
  }
}

export default Choropleth;
